import React, { Component } from 'react';
import {connect} from 'react-redux';

class Header extends Component {
  render() {
    return(
      <div>
        <h2>Merci pour votre like !</h2>
        <p>{this.props.likesToDisplay} like • 1 crush • 1 alert</p>
      </div>
    )
  }
}


function mapStateToProps(state) {
  return { likesToDisplay: state.increaselikes }
}
  
export default connect(
  mapStateToProps, 
  null
)(Header);
