import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';

class App extends Component {
  render() {
    return(
      <div style={{color:'white'}}>
        <h1>Exercice React - Redux</h1>
        <p>Le but est d'incrémenter les différents compteurs quand on clique sur les différents boutons, le tout en utilisants Redux et non les state</p>
        <br/>
        <div style={{backgroundColor:'#515f6c', color:'black', paddingLeft:30, paddingTop:30}}>
          <Header />
          <img src='https://i.imgur.com/INjN2Fs.jpg' style={{width:800}}></img>
        </div>
      </div>
    )
  }
}


class Header extends Component {
  render() {
    return(
      <div>
        <h2>Merci pour votre like !</h2>
        <p>1 like • 1 crush • 1 alert</p>
      </div>
    )
  }
}
export default App;
