// import React, { Component } from 'react';

export default function(likes = 0, action) {
  
  if(action.type === 'increaselikes') {
    // console.log('1) on est dans le reducer increaselikes : ', likes);
    var newLikes = likes + 1;
    return newLikes;
  } else {
    //   console.log('2) on est dans le reducer increaselikes : ', likes);
    return likes;
  }
  
}
