export default function(alerts = 0, action) {
  
  if(action.type === 'increasealerts') {
    // console.log('1) On est dans le reducer increasealerts : ', alerts);
    var newAlerts = alerts + 1;
    return newAlerts;
  } else {
    // console.log('2) On est dans le reducer increasealerts : ', alerts);
    return alerts;
  }
  
}
