export default function(hearts = 0, action) {
  
  if(action.type === 'increaseheart') {
    console.log('1) On est dans le reducer increasehearts : ', hearts);
    var newHearts = hearts + 1;
    return newHearts;
  } else {
    console.log('2) On est dans le reducer increasehearts : ', hearts);
    return hearts;
  }
  
}
