import React, { Component } from 'react';
import './App.css';
import Header from './header';
import Footer from './footer'


// Imports Redux :
import increaselikes from './increaselikes.reducer';
import increasehearts from './increasehearts.reducer';
import increasealerts from './increasealerts.reducer';
import {Provider} from 'react-redux';
import {createStore, combineReducers}  from 'redux';

const store = createStore(combineReducers({increaselikes, increasehearts, increasealerts}));


export default class App extends Component {
  render() {
    return(

      // Redux store
      <Provider store={store}>

          <div style={{color:'white'}}>

            <h1>Exercice React - Redux</h1>
            <p>Le but est d'incrémenter les différents compteurs quand on clique sur les différents boutons, le tout en utilisant Redux et non les state</p>
            <br/>


            <div style={{backgroundColor:'#515f6c', color:'black', paddingLeft:30, paddingTop:30, paddingBottom:40}}>


              {/* Component Header : */}
              <Header />


              <img src='https://i.imgur.com/INjN2Fs.jpg' style={{width:800}} alt="flowers tulip fields"></img> 

              {/* Component footer : */}
              <Footer />


            </div>

          </div>

      </Provider>
    )
  }
}
