import React, { Component } from 'react';
import './App.css';
import { FaRegHeart, FaThumbsUp, FaExclamationTriangle } from "react-icons/fa";
import {connect} from 'react-redux';

class App extends Component {
  render() {
    return(
      <div style={{color:'white'}}>

        <h1>Exercice React - Redux</h1>
        <p>Le but est d'incrémenter les différents compteurs quand on clique sur les différents boutons, le tout en utilisant Redux et non les state</p>
        <br/>


        <div style={{backgroundColor:'#515f6c', color:'black', paddingLeft:30, paddingTop:30, paddingBottom:40}}>


          {/* Composant Header : */}
          <Header />


          <img src='https://i.imgur.com/INjN2Fs.jpg' style={{width:800}} alt="flowers with sunset"></img> 

          {/* Composant footer : */}
          <Footer />


        </div>

      </div>
    )
  }
}


class Header extends Component {
  render() {
    return(
      <div>
        <h2>Merci pour votre like !</h2>
        <p>{this.props.likesToDisplay} like • 1 crush • 1 alert</p>
      </div>
    )
  }
}


class Footer extends Component {
  render() {
    return(
      <div>
         <FaThumbsUp
          style={{marginRight:30, color:'blue', cursor:'pointer'}}
          onClick={ this.props.onIncreaseLikes }
         />
         <FaRegHeart
          style={{marginRight:30,color:'pink', cursor:'pointer'}}
          onClick={ this.props.onIncreaseHearts }

         />
         <FaExclamationTriangle 
          style={{marginRight:30,color:'orange', cursor:'pointer'}}
          onClick={ this.props.onIncreaseAlerts }

         />
      </div>
    )
  }
}

//Envoi des actions
function mapDispatchToProps(dispatch) {
  return {
    onIncreaseLikes: function() { 
        dispatch( {type: 'increaselikes'} )
    },
    onIncreaseHearts: function() { 
        dispatch( {type: 'increasehearts'} )
    },
    onIncreaseAlerts: function() { 
        dispatch( {type: 'increasealerts'} )
    } 
  }
}

//Lecture du store
function mapStateToProps(state) {
  return { likesToDisplay: state.likes }
}



export default connect(
    mapStateToProps, 
    mapDispatchToProps
)(App);
