import React, { Component } from 'react';
import { FaRegHeart, FaThumbsUp, FaExclamationTriangle } from "react-icons/fa";
import {connect} from 'react-redux';

export default class Footer extends Component {
  render() {
    return(
      <div>
         <FaThumbsUp
          style={{marginRight:30, color:'blue', cursor:'pointer'}}
          onClick={ this.props.onIncreaseLikes }
         />
         <FaRegHeart
          style={{marginRight:30,color:'pink', cursor:'pointer'}}
          onClick={ this.props.onIncreaseHearts }

         />
         <FaExclamationTriangle 
          style={{marginRight:30,color:'orange', cursor:'pointer'}}
          onClick={ this.props.onIncreaseAlerts }

         />
      </div>
    )
  }
}
