// import React, { Component } from 'react';

export default function(increaselikes = 0, action) {
  
  if(action.type === 'increaselikes') {
    console.log('1) on est dans le reducer increaselikes : ')
    var newLikes = increaselikes + 1;
    return newLikes;
  } else {
      console.log('2) on est dans le reducer increaselikes : ')
    return increaselikes;
  }
  
}
